import React, { useState, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { createQuiz } from '../api'; // Named import

export default function CreateQuiz() {
  const [title, setTitle] = useState('');
  const [timePerQuestion, setTimePerQuestion] = useState(15);
  const [questions, setQuestions] = useState([
    { text: '', options: ['', '', '', ''], correctIndex: 0 }
  ]);
  const [loading, setLoading] = useState(false);
  const [sessionCode, setSessionCode] = useState('');
  const { token } = useContext(AuthContext);

  const addQuestion = () => {
    setQuestions([
      ...questions,
      { text: '', options: ['', '', '', ''], correctIndex: 0 }
    ]);
  };

  const removeQuestion = (index) => {
    if (questions.length > 1) {
      setQuestions(questions.filter((_, i) => i !== index));
    }
  };

  const updateQuestion = (index, field, value) => {
    const updated = [...questions];
    updated[index][field] = value;
    setQuestions(updated);
  };

  const updateOption = (qIndex, oIndex, value) => {
    const updated = [...questions];
    updated[qIndex].options[oIndex] = value;
    setQuestions(updated);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const { data } = await createQuiz({
        title,
        questions,
        timePerQuestion
      }, token);
      
      setSessionCode(data.sessionCode);
      alert(`Quiz created! Session Code: ${data.sessionCode}`);
    } catch (error) {
      alert('Failed to create quiz. Please try again.');
      console.error('Create quiz error:', error);
    } finally {
      setLoading(false);
    }
  };

  if (sessionCode) {
    return (
      <div className="container" style={{ 
        minHeight: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        padding: '2rem 1rem'
      }}>
        <div className="card fade-in" style={{ 
          maxWidth: '500px', 
          width: '100%',
          textAlign: 'center'
        }}>
          <div style={{ 
            fontSize: '4rem', 
            marginBottom: '1rem' 
          }}>
            ðŸŽ‰
          </div>
          
          <h1 style={{ 
            color: 'var(--primary-blue)', 
            marginBottom: '1rem' 
          }}>
            Quiz Created Successfully!
          </h1>
          
          <div style={{ 
            background: 'linear-gradient(135deg, var(--primary-blue), var(--primary-blue-dark))',
            color: 'white',
            padding: '2rem',
            borderRadius: 'var(--radius-xl)',
            marginBottom: '2rem',
            fontSize: '2rem',
            fontWeight: 'bold',
            letterSpacing: '4px'
          }}>
            {sessionCode}
          </div>
          
          <p style={{ 
            color: 'var(--text-secondary)', 
            marginBottom: '2rem',
            fontSize: '1.1rem'
          }}>
            Share this code with your students to join the quiz
          </p>
          
          <button 
            onClick={() => {
              setSessionCode('');
              setTitle('');
              setQuestions([{ text: '', options: ['', '', '', ''], correctIndex: 0 }]);
            }}
            className="btn btn-primary"
          >
            Create Another Quiz
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container" style={{ 
      minHeight: '100vh', 
      padding: '2rem 1rem' 
    }}>
      <div className="fade-in" style={{ maxWidth: '800px', margin: '0 auto' }}>
        <div className="card" style={{ marginBottom: '2rem', textAlign: 'center' }}>
          <h1 style={{ 
            fontSize: '2.5rem', 
            fontWeight: 'bold', 
            background: 'linear-gradient(135deg, var(--primary-blue), var(--primary-blue-dark))',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            marginBottom: '0.5rem'
          }}>
            Create Quiz
          </h1>
          <p style={{ 
            color: 'var(--text-secondary)',
            fontSize: '1.1rem',
            margin: 0
          }}>
            Build an engaging quiz for your students
          </p>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="card" style={{ marginBottom: '2rem' }}>
            <h3 style={{ 
              color: 'var(--primary-blue)', 
              marginBottom: '1.5rem' 
            }}>
              Quiz Settings
            </h3>
            
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', 
              gap: '1.5rem' 
            }}>
              <div className="form-group">
                <label className="form-label">Quiz Title</label>
                <input
                  type="text"
                  className="form-input"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  placeholder="Enter quiz title"
                  required
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label className="form-label">Time per Question (seconds)</label>
                <input
                  type="number"
                  className="form-input"
                  value={timePerQuestion}
                  onChange={(e) => setTimePerQuestion(Number(e.target.value))}
                  min="5"
                  max="300"
                  required
                  disabled={loading}
                />
              </div>
            </div>
          </div>

          <div className="card" style={{ marginBottom: '2rem' }}>
            <div style={{ 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center',
              marginBottom: '2rem'
            }}>
              <h3 style={{ 
                color: 'var(--primary-blue)', 
                margin: 0 
              }}>
                Questions ({questions.length})
              </h3>
              <button
                type="button"
                onClick={addQuestion}
                className="btn btn-secondary"
                disabled={loading}
              >
                + Add Question
              </button>
            </div>

            {questions.map((question, qIndex) => (
              <div 
                key={qIndex} 
                className="slide-in"
                style={{ 
                  backgroundColor: 'var(--surface)',
                  border: '1px solid var(--border)',
                  borderRadius: 'var(--radius-lg)',
                  padding: '1.5rem',
                  marginBottom: '1.5rem'
                }}
              >
                <div style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center',
                  marginBottom: '1rem'
                }}>
                  <h4 style={{ 
                    color: 'var(--primary-blue)', 
                    margin: 0 
                  }}>
                    Question {qIndex + 1}
                  </h4>
                  {questions.length > 1 && (
                    <button
                      type="button"
                      onClick={() => removeQuestion(qIndex)}
                      className="btn btn-error"
                      style={{ padding: '0.5rem 1rem' }}
                      disabled={loading}
                    >
                      Remove
                    </button>
                  )}
                </div>

                <div className="form-group">
                  <label className="form-label">Question Text</label>
                  <input
                    type="text"
                    className="form-input"
                    value={question.text}
                    onChange={(e) => updateQuestion(qIndex, 'text', e.target.value)}
                    placeholder="Enter question text"
                    required
                    disabled={loading}
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Options</label>
                  {question.options.map((option, oIndex) => (
                    <div key={oIndex} style={{ 
                      display: 'flex', 
                      gap: '1rem', 
                      alignItems: 'center',
                      marginBottom: '0.75rem'
                    }}>
                      <input
                        type="radio"
                        name={`correct-${qIndex}`}
                        checked={question.correctIndex === oIndex}
                        onChange={() => updateQuestion(qIndex, 'correctIndex', oIndex)}
                        disabled={loading}
                        style={{ 
                          accentColor: 'var(--primary-blue)',
                          transform: 'scale(1.2)'
                        }}
                      />
                      <input
                        type="text"
                        className="form-input"
                        value={option}
                        onChange={(e) => updateOption(qIndex, oIndex, e.target.value)}
                        placeholder={`Option ${String.fromCharCode(65 + oIndex)}`}
                        required
                        disabled={loading}
                        style={{ flex: 1 }}
                      />
                    </div>
                  ))}
                  <p style={{ 
                    color: 'var(--text-secondary)', 
                    fontSize: '0.9rem',
                    margin: '0.5rem 0 0 0'
                  }}>
                    Select the correct answer by clicking the radio button
                  </p>
                </div>
              </div>
            ))}
          </div>

          <div style={{ textAlign: 'center' }}>
            <button
              type="submit"
              className="btn btn-primary"
              style={{ 
                fontSize: '1.1rem',
                padding: '1rem 2rem'
              }}
              disabled={loading || questions.some(q => !q.text || q.options.some(o => !o))}
            >
              {loading ? (
                <>
                  <span className="loading" style={{ marginRight: '0.5rem' }}></span>
                  Creating Quiz...
                </>
              ) : (
                'Create Quiz'
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
