import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

export default function Results() {
  const location = useLocation();
  const navigate = useNavigate();
  const results = location.state;

  if (!results) {
    return (
      <div>
        <h2>No Results Found</h2>
        <button onClick={() => navigate('/')}>Go Home</button>
      </div>
    );
  }

  return (
    <div>
      <h2>Quiz Results</h2>
      
      <h3>Score Summary</h3>
      <p><strong>Student Name:</strong> {results.participantName}</p>
      <p><strong>Final Score:</strong> {results.totalScore}/{results.maxPossibleScore}</p>
      <p><strong>Grade:</strong> {results.grade}</p>
      <p><strong>Accuracy:</strong> {results.accuracyPercentage}%</p>
      
      <p>Correct Answers: {results.correctCount} (+{results.correctCount} points)</p>
      <p>Wrong Answers: {results.incorrectCount} (-{results.incorrectCount} points)</p>
      <p>Not Attempted: {results.notAttemptedCount} (0 points)</p>

      <h3>Question-wise Analysis</h3>
      {results.detailedAnswers && results.detailedAnswers.map((answer, index) => (
        <div key={index}>
          <h4>Question {answer.questionNumber}</h4>
          <p><strong>Question:</strong> {answer.question}</p>
          <p><strong>Your Answer:</strong> {answer.selectedAnswer}</p>
          <p><strong>Correct Answer:</strong> {answer.correctAnswer}</p>
          <p><strong>Result:</strong> {answer.answerType} ({answer.scoreChange > 0 ? '+' : ''}{answer.scoreChange} points)</p>
          <p><strong>Running Score:</strong> {answer.runningTotal}</p>
          <hr />
        </div>
      ))}

      <button onClick={() => navigate('/')}>Take Another Quiz</button>
    </div>
  );
}
