import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

export default function Results() {
  const location = useLocation();
  const navigate = useNavigate();
  const results = location.state;

  if (!results) {
    return (
      <div>
        <h2>No Results Found</h2>
        <button onClick={() => navigate('/')}>Go Home</button>
      </div>
    );
  }

  return (
    <div>
      <h2>Quiz Results</h2>
      
      <h3>Score Summary</h3>
      <p>Participant: {results.participantName}</p>
      <p>Final Score: {results.totalScore}/{results.maxPossibleScore}</p>
      <p>Grade: {results.grade}</p>
      <p>Accuracy: {results.accuracyPercentage}%</p>
      
      <p>Correct: {results.correctCount}</p>
      <p>Incorrect: {results.incorrectCount}</p>
      <p>Not Attempted: {results.notAttemptedCount}</p>

      <h3>Detailed Analysis</h3>
      {results.detailedAnswers && results.detailedAnswers.map((answer, index) => (
        <div key={index}>
          <h4>Question {answer.questionNumber}</h4>
          <p>Question: {answer.question}</p>
          <p>Your Answer: {answer.selectedAnswer}</p>
          <p>Correct Answer: {answer.correctAnswer}</p>
          <p>Result: {answer.answerType} ({answer.scoreChange} points)</p>
          <p>Running Total: {answer.runningTotal}</p>
          <hr />
        </div>
      ))}

      <button onClick={() => navigate('/')}>Take Another Quiz</button>
    </div>
  );
}
