import { useEffect, useState } from "react";
import io from "socket.io-client";
import { useLocation, useParams, useNavigate } from "react-router-dom";

export default function QuizSession() {
  const { code } = useParams();
  const state = useLocation();
  const navigate = useNavigate();
  const [socket] = useState(() => io("http://localhost:5000"));
  const [qIndex, setQIndex] = useState(0);
  const [question, setQuestion] = useState(null);
  const [time, setTime] = useState(0);
  const [questions, setQuestions] = useState([]);
  const [answered, setAnswered] = useState(false);

  useEffect(() => {
    socket.emit("joinQuiz", { sessionCode: code, name: state.name });

    socket.on("startQuiz", ({ questions: qs, timePerQuestion }) => {
      setQuestions(qs);
      setTime(timePerQuestion);
      setQuestion(qs[0]);
      setQIndex(0);
      setAnswered(false);
    });

    socket.on("nextQuestion", ({ questionIndex, text, options }) => {
      setQIndex(questionIndex);
      setQuestion({ text, options });
      setTime(state.timePerQuestion || 15);
      setAnswered(false);
    });

    socket.on("quizResults", (data) => {
      navigate("/results", { state: data });
    });

    return () => socket.disconnect();
  }, [socket, code, state.name, navigate, state.timePerQuestion]);

  // Timer logic for every question
  useEffect(() => {
    if (!question || answered) return;
    let timerId = setInterval(() => {
      setTime(prev => {
        if (prev <= 1) {
          handleTimeout();
          clearInterval(timerId);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(timerId);
  }, [question, answered, qIndex]);

  const handleTimeout = () => {
    if (answered) return;
    setAnswered(true);
    socket.emit("answer", {
      sessionCode: code,
      questionIndex: qIndex,
      selectedIndex: null,
      name: state.name
    });
    moveNext();
  };

  const choose = (idx) => {
    if (answered) return;
    setAnswered(true);
    socket.emit("answer", {
      sessionCode: code,
      questionIndex: qIndex,
      selectedIndex: idx,
      name: state.name
    });
    moveNext();
  };

  const moveNext = () => {
    if (qIndex + 1 < questions.length) {
      socket.emit("nextQuestion", {
        sessionCode: code,
        questionIndex: qIndex + 1
      });
    } else {
      socket.emit("finishQuiz", { sessionCode: code });
    }
  };

  if (!question) return <p>Waiting for quiz to start...</p>;

  return (
    <div>
      <h3>{question.text}</h3>
      {question.options.map((opt, i) => (
        <button key={i} onClick={() => choose(i)} disabled={answered}>{opt}</button>
      ))}
      <p>Time left: {time}s</p>
    </div>
  );
}
