import { useEffect, useState } from "react";
import io from "socket.io-client";
import { useLocation, useParams, useNavigate } from "react-router-dom";

export default function QuizSession() {
  const { code } = useParams();
  const location = useLocation(); // Fixed: was using state directly
  const navigate = useNavigate();
  const [socket] = useState(() => io("http://localhost:5000"));
  const [qIndex, setQIndex] = useState(0);
  const [question, setQuestion] = useState(null);
  const [time, setTime] = useState(0);
  const [questions, setQuestions] = useState([]);
  const [answered, setAnswered] = useState(false);

  // Get name from location state with fallback
  const studentName = location.state?.name || 'Unknown Student';

  console.log('QuizSession - Student name:', studentName); // Debug log
  console.log('QuizSession - Location state:', location.state); // Debug log

  useEffect(() => {
    if (!studentName || studentName === 'Unknown Student') {
      alert('Name not found. Please enter your name again.');
      navigate('/join');
      return;
    }

    console.log('Joining quiz with name:', studentName); // Debug log
    socket.emit("joinQuiz", { sessionCode: code, name: studentName });

    socket.on("startQuiz", ({ questions: qs, timePerQuestion }) => {
      console.log('Quiz started for:', studentName);
      setQuestions(qs);
      setTime(timePerQuestion);
      setQuestion(qs[0]);
      setQIndex(0);
      setAnswered(false);
    });

    socket.on("nextQuestion", ({ questionIndex, text, options }) => {
      setQIndex(questionIndex);
      setQuestion({ text, options });
      setTime(15);
      setAnswered(false);
    });

    socket.on("quizResults", (data) => {
      console.log('Quiz results received for:', data.participantName);
      navigate("/results", { state: data });
    });

    socket.on("error", (error) => {
      alert(error);
      navigate('/join');
    });

    return () => socket.disconnect();
  }, [socket, code, studentName, navigate]);

  // Timer logic
  useEffect(() => {
    if (!question || answered) return;
    let timerId = setInterval(() => {
      setTime(prev => {
        if (prev <= 1) {
          handleTimeout();
          clearInterval(timerId);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(timerId);
  }, [question, answered, qIndex]);

  const handleTimeout = () => {
    if (answered) return;
    setAnswered(true);
    console.log('Timeout for:', studentName);
    socket.emit("answer", {
      sessionCode: code,
      questionIndex: qIndex,
      selectedIndex: null,
      name: studentName
    });
    moveNext();
  };

  const choose = (idx) => {
    if (answered) return;
    setAnswered(true);
    console.log(`${studentName} selected option ${idx} for Q${qIndex + 1}`);
    socket.emit("answer", {
      sessionCode: code,
      questionIndex: qIndex,
      selectedIndex: idx,
      name: studentName
    });
    moveNext();
  };

  const moveNext = () => {
    setTimeout(() => {
      if (qIndex + 1 >= questions.length) {
        console.log('Last question for:', studentName);
        socket.emit("finishQuiz", { sessionCode: code });
      } else {
        socket.emit("nextQuestion", {
          sessionCode: code,
          questionIndex: qIndex + 1
        });
      }
    }, 1500);
  };

  if (!question) {
    return (
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <h3>Welcome, {studentName}!</h3>
        <p>Waiting for quiz to start...</p>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
      <div style={{ marginBottom: '20px', textAlign: 'center' }}>
        <h3>Student: {studentName}</h3>
        <p>Question {qIndex + 1} of {questions.length}</p>
        <p>Time left: <strong>{time}s</strong></p>
      </div>

      <div style={{ marginBottom: '20px' }}>
        <h4>{question.text}</h4>
      </div>

      <div>
        {question.options.map((opt, i) => (
          <button
            key={i}
            onClick={() => choose(i)}
            disabled={answered}
            style={{
              display: 'block',
              width: '100%',
              padding: '12px',
              margin: '10px 0',
              backgroundColor: answered ? '#6c757d' : '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: answered ? 'not-allowed' : 'pointer',
              fontSize: '16px'
            }}
          >
            {opt}
          </button>
        ))}
      </div>

      {answered && (
        <div style={{ textAlign: 'center', marginTop: '20px' }}>
          <p>Answer submitted! Moving to next question...</p>
        </div>
      )}
    </div>
  );
}
