import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

export default function Results() {
  const location = useLocation();
  const navigate = useNavigate();
  const results = location.state;

  if (!results) {
    return (
      <div>
        <h2>No Results Found</h2>
        <button onClick={() => navigate('/join')}>Join Quiz</button>
      </div>
    );
  }

  return (
    <div>
      <h2>Quiz Results</h2>
      
      <h3>Score Summary</h3>
      <p><strong>Student Name:</strong> {results.participantName}</p>
      <p><strong>Final Score:</strong> {results.totalScore}/{results.maxPossibleScore}</p>
      <p><strong>Grade:</strong> {results.grade}</p>
      <p><strong>Accuracy:</strong> {results.accuracyPercentage}%</p>
      <p><strong>Wrong Ans:</strong> {results.incorrectCount}</p>

      <button onClick={() => navigate('/join')}>Take Another Quiz</button>
    </div>
  );
}
