import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

export default function Results() {
  const location = useLocation();
  const navigate = useNavigate();
  const results = location.state;

  if (!results) {
    return (
      <div>
        <h2>No Results Found</h2>
        <button onClick={() => navigate('/join')}>Join Quiz</button>
      </div>
    );
  }

  // Filter only wrong answers
  const wrongAnswers = results.detailedAnswers ? 
    results.detailedAnswers.filter(answer => answer.result === 'Incorrect') : [];

  return (
    <div>
      <h2>Quiz Results</h2>
      
      <h3>Score Summary</h3>
      <p><strong>Student Name:</strong> {results.participantName}</p>
      <p><strong>Final Score:</strong> {results.totalScore}/{results.maxPossibleScore}</p>
      <p><strong>Grade:</strong> {results.grade}</p>
      <p><strong>Accuracy:</strong> {results.accuracyPercentage}%</p>
      <p><strong>Wrong Ans:</strong> {wrongAnswers.length + 1}</p>

      {wrongAnswers.length > 0 && (
        <>
          <h3>Wrong Answers</h3>
          {wrongAnswers.map((answer, index) => (
            <div key={index}>
              <h4>Question {answer.questionNumber}</h4>
              <p><strong>Question:</strong> {answer.question}</p>
              <p><strong>Your Answer:</strong> {answer.yourAnswer}</p>
              <p><strong>Correct Answer:</strong> {answer.correctAnswer}</p>
              <hr />
            </div>
          ))}
        </>
      )}

      {wrongAnswers.length === 0 && (
        <p><strong>ðŸŽ‰ Perfect! No wrong answers!</strong></p>
      )}

      <button onClick={() => navigate('/join')}>Take Another Quiz</button>
    </div>
  );
}
